import { Component, OnInit } from '@angular/core';
import { Employee } from './../../employee-model/employee';
import { EmployeeServiceService } from './../../employee-service/employee-service.service';
import { catchError, Observable, of, Subject } from 'rxjs';

@Component({
  selector: 'app-employee-grid-table',
  templateUrl: './employee-grid-table.component.html',
  styleUrls: ['./employee-grid-table.component.css']
})
export class EmployeeGridTableComponent implements OnInit {
 // employeeAllStream!:Observable<Employee[]>;
  employeeAllStream!:Employee[];
  employeeStream!:Observable<Employee>;
  employeeStreamError=new Subject<boolean>();
  Value:string='';

  constructor(private employeeService:EmployeeServiceService) { }

  ngOnInit(): void {
 this.getALlEmployees();
  }

  public getALlEmployees(){
    this.employeeService.getAllEmployees().subscribe((response): void=>{
      this.employeeAllStream=response;
    })
    }

    public getEmployeeById(eid:number){
      this.employeeStream=this.employeeService.getEmployeeById(eid).pipe(
        catchError((error) => {
          console.log('error',error)
          this.employeeStreamError.next(true);
          return of();
        }),
      );
    }
  

public deleteEmployee(data:Employee){
this.employeeService.deleteById(data.eid?data.eid:0).subscribe(
res=>console.log(res),
error=> console.log(error),
()=> this.getALlEmployees()
);
}

}


  


<div class="text-center">
 <h2>Employees Grid Table</h2> 
 <!-- <div *ngIf="employeeAllStream | async"></div> -->
 <div *ngIf="employeeStream | async"></div>

</div>
<div class=" mt-4 text-center">
  <input type="search" class="search" placeholder="Search Employee By Name........" [(ngModel)]="Value" aria-label="Search" >
</div>
<div class="mt-4 text-center container">
<table style="width:100%">
   <thead>
    <tr>
    <th>No.</th>
    <th>First Name</th>
    <th>Last Name</th>
    <th>Email Id</th>
    <th>Address</th>
    <th>Mobile Number</th>
    <th>Edit</th>
    <th>Delete</th>
</tr>
   </thead>
   <tbody *ngFor="let data of employeeAllStream  | filterPipe:Value  , let i = index"> 
<tr>
   <td>{{i+1}}</td> 
<td>{{data.firstName }}</td>
<td>{{data.lastName}}</td>
<td>{{data.emailId}}</td>
<td>{{data.address}}</td>
<td>{{data.mobileNumber}}</td>
<td><i class="fa fa-edit" aria-hidden="true" (click)="getEmployeeById(data.eid)"></i></td>
<td> <i class="fa fa-trash" aria-hidden="true" (click)="deleteEmployee(data)"></i></td>
</tr>
</tbody> 
</table>    

</div>
